import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  Center,
  Image,
} from '@chakra-ui/react';

function ImageUploader() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [returnedUrl, setReturnedUrl] = useState(null);

  const handleFileInput = (event) => {
    setSelectedFile(event.target.files[0]);
    setPreviewUrl(URL.createObjectURL(event.target.files[0]));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/upload-image/',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
          responseType: 'arraybuffer', // add this to receive binary data
        }
      );
      const imgstr = Buffer.from(response.data, 'binary').toString('base64');
      setReturnedUrl(`data:image/png;base64,${imgstr}`);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Box p={4}>
      <Heading as="h2" size="lg" mb={4}>
        Upload an Image
      </Heading>
      <form onSubmit={handleSubmit}>
        <FormControl>
          <FormLabel>Choose a file</FormLabel>
          <Input type="file" onChange={handleFileInput} />
        </FormControl>
        {previewUrl && (
          <Center mt={4}>
            <Box w={512} h={512} borderWidth="1px" borderColor="gray.200">
              <Image src={previewUrl} w="100%" h="100%" objectFit="cover" />
            </Box>
          </Center>
        )}
        {returnedUrl && (
          <Center mt={4}>
            <Box w={512} h={512} borderWidth="1px" borderColor="gray.200">
              <Image src={returnedUrl} w="100%" h="100%" objectFit="cover" />
            </Box>
          </Center>
        )}
        <Button type="submit" mt={4}>
          Upload
        </Button>
      </form>
    </Box>
  );
}

export default ImageUploader;
