import React, { useState } from "react";
import { Box, Text, VStack, Button } from "@chakra-ui/react";
import axios from "axios";

function App() {
  const [data, setData] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  async function fetchData() {
    const formData = new FormData();
    formData.append("file", imageFile);

    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };

    try {
      const response = await axios.post("http://127.0.0.1:8000/get_data", formData, config);
      setData(response.data);
    } catch (error) {
      console.log(error);
    }
  }
  function handleImageFileChange(event) {
    setImageFile(event.target.files[0]);
  }

  return (
    <Box p={4}>
      <input type="file" onChange={handleImageFileChange} />
      <Button mt={4} onClick={fetchData}>
        Fetch Data
      </Button>
      {data &&
        data.images.map((image, index) => (
          <VStack key={index} spacing={4}>
            <Box w="400px">
              <img src={`data:image/png;base64,${image.data}`} alt={image.filename} />
            </Box>
            <Text>{data.text[index]}</Text>
          </VStack>
        ))}
    </Box>
  );
}

export default App;
