import React, { useState } from 'react';
import axios from 'axios';

import { Box } from '@chakra-ui/react';

function ImageUploader() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [rotatedImage, setRotatedImage] = useState(null);

  const handleFileInput = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload-image/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data);
      setRotatedImage(URL.createObjectURL(new Blob([response.data.rotated_image])));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Upload an Image</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileInput} />
        <button type="submit">Upload</button>
      </form>
      {selectedFile && (
        <Box mt={4} display="flex" flexDirection="column" alignItems="center">
          <Box mb={2}>
            <img src={URL.createObjectURL(selectedFile)} alt="Original Image" style={{ maxWidth: "512px", maxHeight: "512px" }} />
          </Box>
          {rotatedImage && (
            <Box>
              <h3>Rotated Image</h3>
              <img src={rotatedImage} alt="Rotated Image" style={{ maxWidth: "512px", maxHeight: "512px" }} />
            </Box>
          )}
        </Box>
      )}
    </div>
  );
}

export default ImageUploader;
