import React, { useState } from "react";
import { Heading, Box, Image, Button } from "@chakra-ui/react";

function App() {
  const [image, setImage] = useState("https://via.placeholder.com/512x512");

  const handleImageChange = (event) => {
    const newImage = URL.createObjectURL(event.target.files[0]);
    setImage(newImage);
  };

  return (
    <Box bg="black" h="100vh" d="flex" alignItems="center" justifyContent="center">
      <Box textAlign="center">
        <Heading color="white" mb={4}>
          Draw my face
        </Heading>
        <Box w="512px" h="512px" m="auto">
          <Image src={image} alt="face image" w="100%" h="100%" />
        </Box>
        <Box mt={4}>
          <Button as="label" htmlFor="fileInput" colorScheme="blue" size="md">
            Upload Image
          </Button>
          <input
            id="fileInput"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            hidden
          />
        </Box>
      </Box>
    </Box>
  );
}

export default App;
