import { Box, Heading, Image, Input, Button, HStack } from '@chakra-ui/react';
import { useState } from 'react';

function App() {
  const [imageSrc, setImageSrc] = useState('https://via.placeholder.com/512x512');
  const [styleSrc, setStyleSrc] = useState('https://via.placeholder.com/512x512');

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      setImageSrc(event.target.result);
    };

    reader.readAsDataURL(file);
  };

  const handleStyleUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      setStyleSrc(event.target.result);
    };

    reader.readAsDataURL(file);
  };

  return (
    <Box bg="black" color="white" textAlign="center" py={10}>
      <Heading as="h1" size="2xl">
        Draw my face
      </Heading>
      <HStack mt={10} spacing={4}>
        <Image src={styleSrc} alt="style image" boxSize="512px" />
        <Image src={imageSrc} alt="uploaded image" boxSize="512px" />
      </HStack>
      <Box mt={10}>
        <Input
          type="file"
          onChange={handleStyleUpload}
          accept="image/*"
          bg="yellow.300"
          color="white"
          borderRadius="full"
          py={4}
          px={8}
          _hover={{ bg: 'yellow.400' }}
          _active={{ bg: 'yellow.500' }}
        />
        <Button onClick={() => {}} mt={2} mx={4}>
          Style
        </Button>
        <Input
          type="file"
          onChange={handleImageUpload}
          accept="image/*"
          bg="yellow.300"
          color="white"
          borderRadius="full"
          py={4}
          px={8}
          _hover={{ bg: 'yellow.400' }}
          _active={{ bg: 'yellow.500' }}
        />
      </Box>
    </Box>
  );
}

export default App;
