const App = () => {
  const [image, updateImage] = useState()
  const [prompt, updatePrompt] = useState()
  const [loading, updateLoading] = useState()

  const generate = async prompt =>{
    updateLoading(true)
    const result = await axios.get(`http://127.0.0.1:8000/?prompt=${prompt}`)
    updateImage(result.data)
    updateLoading(false)
  }

  return (
    <ChakraProvider>
      <Container>
        <Heading>Stable DiffusionðŸš€</Heading>

        <Text marginBottom={"10px"}>Stable diffusion test on a reach application 
        using darkstorm2150 Protogen_v2.2_Official_Release model!!! <Link href="https://huggingface.co/darkstorm2150/Protogen_v2.2_Official_Release">Git Repo</Link></Text>

        <Wrap  marginBottom={"10px"}>
          <Input value={prompt} onChange={e => updatePrompt(e.target.value)} width={"350px"}></Input>
          <Button onClick = {e=>generate(prompt)}  colorScheme={"yellow"}>Generate</Button>
        </Wrap>

        {loading ? (
          <Stack>
            <SkeletonCircle/>
            <SkeletonText/>
          </Stack>
        ) : image ? 
          (<Image src={`data:image/png;base64,${image}`} boxShadow="lg"/>
        ) : null}


      </Container>
    </ChakraProvider>
  )
}

export default App
