import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  Center,
  Image,
  Select,
  HStack
} from '@chakra-ui/react';

function ImageUploader() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [selectedOption, setSelectedOption] = useState("None");

  const handleFileInput = (event) => {
    setSelectedFile(event.target.files[0]);
    setPreviewUrl(URL.createObjectURL(event.target.files[0]));
  };

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('option', selectedOption);

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload-image/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Box  bg="black" color="white" textAlign="center" py={10}>
      <Heading as="h2" size="lg" mb={4}>
        Upload an Image
      </Heading>
      <HStack>
        <form onSubmit={handleSubmit}>
          <FormControl>
            <FormLabel>Choose a file</FormLabel>
            <Input type="file" onChange={handleFileInput} />
          </FormControl>
          <FormControl mt={4}>
            <FormLabel>Select an option</FormLabel>
            <Select value={selectedOption} onChange={handleOptionChange}>
              <option value="None">None</option>
              <option value="Cartoon">Cartoon</option>
            </Select>
          </FormControl>
          {previewUrl && (
            <Center mt={4}>
              <Box w={512} h={512} borderWidth="1px" borderColor="gray.200">
                <Image src={previewUrl} w="100%" h="100%" objectFit="cover" />
              </Box>
            </Center>
          )}
          <Button type="submit" mt={4}>
            Upload
          </Button>
        </form>
        <Box>
          <Image src="https://via.placeholder.com/512x512" alt="placeholder" mt={10} mx="auto" />
        </Box>
      </HStack>
    </Box>
  );
}

export default ImageUploader;
