import React, { useState } from "react";
import {
  Box,
  Button,
  ChakraProvider,
  Container,
  extendTheme,
  Flex,
  FormControl,
  Grid,
  Image,
  VStack,
  Tooltip,
  HStack,
} from "@chakra-ui/react";
import Webcam from "react-webcam";
import axios from "axios";

const theme = extendTheme({
  components: {
    FileInput: {
      baseStyle: {
        input: {
          opacity: 0,
          position: "absolute",
          top: 0,
          left: 0,
          bottom: 0,
          right: 0,
          height: "100%",
          width: "100%",
          cursor: "pointer",
        },
      },
    },
  },
});

function App() {
  const [leftImage, setLeftImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [webcamOpen, setWebcamOpen] = useState(false);
  const [isGenerateHovered, setIsGenerateHovered] = useState(false);
  const [instructionsGenerated, setInstructionsGenerated] = useState(false);
  const [rotatedImages, setRotatedImages] = useState([]);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setLeftImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleGenerate = async () => {
    if (leftImage) {
      try {
        const response = await axios.post(
          "http://localhost:8000/rotate-image/",
          { image: leftImage },
          { headers: { "Content-Type": "application/json" } }
        );
        setRightImage(`data:image/png;base64,${response.data.image}`);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const handleInstructions = async () => {
    if (rightImage) {
      try {
        const response = await axios.post(
          "http://localhost:8000/instructions/",
          { image: rightImage },
          { headers: { "Content-Type": "application/json" } }
        );
        setRotatedImages(response.data.images.map((img) => `data:image/png;base64,${img}`));
      } catch (error) {
        console.error(error);
      }
    }
  };

  const webcamRef = React.useRef(null);
  const capture = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setLeftImage(imageSrc);
    setWebcamOpen(false); // Add this line to close the camera after taking a picture
  };

  return (
    <ChakraProvider theme={theme}>
      <Container maxW="100%" py={5}>
        <Flex alignItems="center" justifyContent="center" flexDirection="column">
        <HStack>
        <Box>
            <VStack spacing={5}>
              {webcamOpen ? (
                <Box>
                  <Webcam
                    audio={false}
                    ref={webcamRef}
                    screenshotFormat="image/png"
                    width={512}
                    height={512}
                  />
                  <Button onClick={capture} mt={2}>
                    Take Picture
                  </Button>
                  <Button onClick={() => setWebcamOpen(false)} mt={2}>
                    Close Camera
                  </Button>
                </Box>
              ) : (
                <Box position="relative" width="512px" height="512px">
                  <Image
                    boxSize="512px"
                    objectFit="cover"
                    src={leftImage || "https://via.placeholder.com/512x512.png?text=Placeholder"}
                    alt="Left Image"
                  />
                  <FormControl>
                  <label htmlFor="file-upload" style={{ position: "absolute", cursor: "pointer" }}>
                    <Button as="span" size="sm">Upload Image</Button>
                  </label>
                  <input
                    type="file"
                    id="file-upload"
                    accept=".png,.jpg,.jpeg"
                    onChange={handleFileChange}
                    style={{ display: "none" }}
                  />
                </FormControl>
                </Box>
              )}
              <Button onClick={() => setWebcamOpen(true)}>Camera</Button>
            </VStack>
          </Box>
          <Box padding="20%">
            <Button
              onClick={() => {
                handleGenerate();
                setInstructionsGenerated(true);
              }}
              disabled={!leftImage || webcamOpen}
              mb={5}
              onMouseEnter={() => setIsGenerateHovered(true)}
              onMouseLeave={() => setIsGenerateHovered(false)}
            >
              Generate
            </Button>
          </Box>
          <Box>
          <Image
            boxSize="512px"
            objectFit="cover"
            src={rightImage || "https://via.placeholder.com/512/0000FF/808080?text=Placeholder"}
            alt="Right Image"
          />
          </Box>
          </HStack>
          <Tooltip
            label="Please generate image first"
            isOpen={!instructionsGenerated && isGenerateHovered}
            hasArrow
          >
            <Button
              onClick={handleInstructions}
              disabled={!instructionsGenerated}
              mb={5}
              onMouseEnter={() => setIsGenerateHovered(true)}
              onMouseLeave={() => setIsGenerateHovered(false)}
            >
              Instructions
            </Button>
          </Tooltip>
          <Grid templateColumns="repeat(5, 1fr)" gap={6} marginBottom={5}>
            {rotatedImages.map((image, index) => (
              <Image key={index} boxSize="100px" src={image} alt={`Rotated Image ${index + 1}`} />
            ))}
          </Grid>
        </Flex>
      </Container>
    </ChakraProvider>
  );
}

export default App;
