import React, { useState } from "react";
import {
  Box,
  Button,
  Center,
  ChakraProvider,
  Container,
  extendTheme,
  Image,
  VStack,
  Tooltip
} from "@chakra-ui/react";
import Webcam from "react-webcam";
import axios from "axios";

const theme = extendTheme({
  components: {
    FileInput: {
      baseStyle: {
        input: {
          opacity: 0,
          position: "absolute",
          top: 0,
          left: 0,
          bottom: 0,
          right: 0,
          height: "100%",
          width: "100%",
          cursor: "pointer",
        },
      },
    },
  },
});

function App() {
  const [leftImage, setLeftImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [webcamOpen, setWebcamOpen] = useState(false);
  const [isGenerateHovered, setIsGenerateHovered] = useState(false);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setLeftImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleGenerate = async () => {
    if (leftImage) {
      try {
        const response = await axios.post(
          "http://localhost:8000/rotate-image/",
          { image: leftImage },
          { headers: { "Content-Type": "application/json" } }
        );
        setRightImage(`data:image/png;base64,${response.data.image}`);
      } catch (error) {
        console.error(error);
      }
    }
  };

  const webcamRef = React.useRef(null);
  const capture = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setLeftImage(imageSrc);
    setWebcamOpen(false); // Add this line to close the camera after taking a picture
  };

  return (
    <ChakraProvider theme={theme}>
      <Container maxW="container.lg" py={5}>
        <Center>
          <VStack spacing={5}>
            {webcamOpen ? (
              <Box>
                <Webcam
                  audio={false}
                  ref={webcamRef}
                  screenshotFormat="image/png"
                  width={512}
                  height={512}
                />
                <Button onClick={capture} mt={2}>
                  Take Picture
                </Button>
                <Button onClick={() => setWebcamOpen(false)} mt={2}>
                  Close Camera
                </Button>
              </Box>
            ) : (
              <Box position="relative" width="512px" height="512px">
                <Image
                  boxSize="512px"
                  objectFit="cover"
                  src={leftImage || "https://via.placeholder.com/512x512.png?text=Placeholder"}
                  alt="Left Image"
                />
                <label htmlFor="file-upload">
                  <Button size="sm" as="span">
                    Upload Image
                  </Button>
                  <input
                    type="file"
                    id="file-upload"
                    accept=".png,.jpg,.jpeg"
                    onChange={handleFileChange}
                    style={{ display: "none" }}
                  />
                </label>
              </Box>
            )}
           <Tooltip
              label="Please upload image first"
              isOpen={!leftImage && isGenerateHovered}
              hasArrow
            >
              <Button
                onClick={handleGenerate}
                disabled={!leftImage || webcamOpen}
                mb={5}
                onMouseEnter={() => setIsGenerateHovered(true)}
                onMouseLeave={() => setIsGenerateHovered(false)}
              >
                Generate
              </Button>
            </Tooltip>
            <Image
              boxSize="512px"
              objectFit="cover"
              src={rightImage || "https://via.placeholder.com/512x512.png?text=Placeholder"}
              alt="Right Image"
            />
            <Button onClick={() => setWebcamOpen(true)}>Camera</Button>
          </VStack>
        </Center>
      </Container>
    </ChakraProvider>
  );
}

export default App;
