import { Box, Heading, Image, Input, Button } from '@chakra-ui/react';
import { useState } from 'react';

function App() {
  const [imageSrc, setImageSrc] = useState('https://via.placeholder.com/512x512');

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      setImageSrc(event.target.result);
    };

    reader.readAsDataURL(file);
  };

  return (
    <Box bg="black" color="white" textAlign="center" py={10}>
      <Heading as="h1" size="2xl">
        Draw my face
      </Heading>
      <Image src={imageSrc} alt="uploaded image" mt={10} mx="auto" boxSize="512px" />
      <Box mt={10}>

        <Button onClick={() => {}} mt={2}>
          Upload
        </Button>
      </Box>
    </Box>
  );
}

export default App;
