import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  Heading,
  FormControl,
  Input,
  Button,
  Center,
  Image,
  HStack,
} from '@chakra-ui/react';

function ImageUploader() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);

  const [image, updateImage] = useState()
  const [prompt, updatePrompt] = useState()
  const [loading, updateLoading] = useState()

  const handleFileInput = (event) => {
    setSelectedFile(event.target.files[0]);
    setPreviewUrl(URL.createObjectURL(event.target.files[0]));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload-image/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const generate = async prompt =>{
    updateLoading(true)
    const result = await axios.get(`http://127.0.0.1:8000/upload-image/?prompt=${prompt}`)
    updateImage(result.data)
    updateLoading(false)
  }

  return (
    <Box bg="black" color="white" textAlign="center" py={10}>
      <Heading as="h2" size="lg" mb={4}>
        Draw My Face
      </Heading>
      <HStack alignItems="center" justifyContent="center">
        <form onSubmit={handleSubmit}>
            <FormControl>
              <Input type="file" onChange={handleFileInput} />
            </FormControl>
            {previewUrl ? (
              <Center mt={4}>
                <Box w={512} h={512} borderWidth="1px" borderColor="gray.200">
                  <Image src={previewUrl} w="100%" h="100%" objectFit="cover" />
                </Box>
              </Center>
            ) : (
              <Center mt={4}>
                <Box w={512} h={512} borderWidth="1px" borderColor="gray.200">
                  <Image src="https://via.placeholder.com/512x512.png?text=Placeholder" w="100%" h="100%" objectFit="cover" />
                </Box>
              </Center>
            )}

            <Button onClick = {e=>generate(prompt)} mt={4}>
              Generate
            </Button>

            <Button onClick = {e=>handleSubmit()} mt={4}>
              Generate
            </Button>
        </form>
        <Box>
          <div>
            <label>
              Select Style
              <select>
                <option value={prompt} onChange={e => updatePrompt(e.target.value)}>None</option>
                <option value={prompt} onChange={e => updatePrompt(e.target.value)}>Anime</option>
                <option value={prompt} onChange={e => updatePrompt(e.target.value)}>Cartoon</option>
              </select>
            </label>
          </div>
          <Image src="https://via.placeholder.com/512x512" alt="placeholder" mt={10} mx="auto" />
        </Box>
      </HStack>
    </Box>
  );
}

export default ImageUploader;
